mapper.update:
update tb_pils_mbr
set nm            = #{name}
  , mbphno        = #{mbphno}
  , psitn_dept_nm = #{deptNm}
  , pstn          = #{position}
  , email_addr    = #{email}
  , telno         = #{memTelNo}
  , faxno         = #{memFaxNo}
  , upd_dt        = current_date
where user_id = #{id}

update tb_pils_mbr
set pswd = encode(encrypt(convert_to(#{passwd}, 'utf8'), 'ENC_KEY', 'aes'), 'hex')
where user_id = #{id}

mapper.insert:
insert into tb_pils_trace( title
                         , cont
                         , rgst_dt)
values ( #{title}
       , #{cont}
       , current_timestamp)

    SELECT MAX(seq) FROM tb_pils_trace;

mapper.update:
update tb_pils_trace
set title =#{title}
  , cont  =#{cont}
where seq = #{seq}

update tb_pils_banner
ban_nm = #{banNm}, site_url = #{siteUrl}, sort_seq = #{sortSeq}, up_dt = NOW(), file_nm =#{fileNm}, file_size =#{fileSize}, file_ty = #{fileType},
pc_img_path = #{pcImgPath},
mobile_img_path = #{mobileImgPath}
where ban_sn = #{banSeq}

update tb_pils_banner
set stts = #{stts}
where ban_sn = ${banSeq}

mapper.select:
select *
from tb_pils_qna_contact
where seq = #{seq}
