mapper.select:
SELECT COUNT(1)
from tb_pils_cmp_agre
where type_cd = #{type_cd}

mapper.update:
update tb_pils_cmp_agre
set name = #{name}
    , prmy_mat = #{prmy_mat}
    , prmy_mat_std  = #{prmy_mat_std}
    , std_point = #{std_point}
    , change_point = #{change_point}
    , rqrm_ust  = #{rqrm_ust}
    , rqrm_cycl = #{rqrm_cycl}
    , rqrm_date = #{rqrm_date}
    , reflect_point = #{reflect_point}
    , formula  = #{formula}
    , ratio  = #{ratio}
    , etc = #{etc}
    , ex_nm  = #{ex_nm}
    , regist_dt =NOW()
where seq = #{seq}

mapper.select:
select coalesce(max(id), '0') as id
from  tb_pils_cmp_agre
where id = #{id}
