mapper.select:
SELECT T.* FROM ( SELECT B.BBS_SN AS board_seq , B.BBS_ID AS bbs_id , B.STTS AS stat , ( SELECT CD_NM FROM tb_cd_data WHERE cd_grp = '90000' AND CD = B.STTS) as STAT_NM /*fn_getcodename*/ , B.TTL AS title , B.REGSTR_NM AS reg_nm , B.REGIST_DATE AS reg_de , TO_CHAR(B.REGIST_DATE, 'YYYY.MM.DD') AS upd_de , B.BEGIN_AT AS opn_at , B.REDNG_NMBRS AS read_cnt , B.CNNC AS lnk , B.STTS AS stat , B.notice_at as notiAt FROM TB_BBS_INFO B WHERE 1 = 1 AND B.BBS_ID = #{bbsId} AND B.STTS = #{stat}
AND (B.TTL LIKE CONCAT('%', #{searchKeyword}, '%') OR B.REGSTR_NM LIKE CONCAT('%', #{searchKeyword}, '%'))
) T WHERE 1 = 1
ORDER BY case when T.notiAt ='Y' then 1 end, T.reg_de desc
LIMIT #{pageUnit} OFFSET #{firstIndex}

SELECT COUNT(1)
FROM TB_BBS_INFO B
WHERE 1 = 1
    AND B.STTS = #{stat}
    AND B.BBS_ID = #{bbsId}

AND (B.TTL LIKE CONCAT('%', #{searchKeyword}, '%') OR B.REGSTR_NM LIKE CONCAT('%', #{searchKeyword}, '%'))

mapper.update:
UPDATE TB_BBS_INFO SET
    STTS = #{stat}
WHERE 1=1
    AND BBS_SN = #{boardSeq}

UPDATE TB_BBS_INFO SET
REDNG_NMBRS = REDNG_NMBRS + 1
WHERE BBS_SN = #{boardSeq}
