mapper.insert:
insert into tb_pils_cmpmbr
( cmp_nm
, bizno
, cmp_mbr_cd
, ceo
, fdate
, addr
, addr_dtl
, telno
, faxno
, biz_ty
, capl
, deal_ty
, yr_deal_scale
, prmy_mat
, con_q
, appdt
, product
, joincmp
, main_product)
values ( #{cmpNm}
       , #{bizNo}
       , #{mem_cd}
       , #{ceo}
       , #{fdate}
       , #{address}
       , #{address_dtl}
       , #{telNo}
       , #{telNo}
       , #{bizType}
       , #{capital}
       , #{deal_type}
       , #{deal_scale}
       , #{material}
       , #{conQ}
       , current_timestamp
       , #{product}
       , #{joinCmp}
       , #{mainProduct})

insert into tb_pils_mbr
( nm
, mbphno
, psitn_dept_nm
, pstn
, email_addr
, telno
, faxno
, auth_cd
, bizno
, user_id
, pswd
, join_dt
, manage_cd)
values ( #{name}
       , #{mbphno}
       , #{deptNm}
       , #{position}
       , #{email}
       , #{memTelNo}
       , #{memFaxNo}
       , 'M103'
       , #{bizNo}
       , #{id}
       , encode(encrypt(convert_to(#{passwd}, 'utf8'), 'ENC_KEY', 'aes'), 'hex')
       , current_date
       , #{management_cd})

mapper.select:
SELECT COUNT(1)
FROM tb_pils_cmpmbr A
where cmp_mbr_cd = #{mem_cd}

SELECT sum(joincmp)
FROM tb_pils_cmpmbr
WHERE cmp_mbr_cd = #{mem_cd}

select case
           when inPass = pswd then 1
           else 0 end as rslt
from (select encode(encrypt(convert_to(#{passwd}, 'utf8'), 'ENC_KEY', 'aes'), 'hex') as inPass
           , pswd
      from tb_pils_mbr
      where bizno = #{bizNo}
        and manage_cd = 'M501'
     ) a;

mapper.update:
update tb_pils_cmpmbr
set cmp_nm        = #{cmpNm}
  , bizno         = #{bizNo}
  , ceo           = #{ceo}
  , fdate         = #{fdate}
  , addr          = #{address}
  , addr_dtl      = #{address_dtl}
  , telno         = #{telNo}
  , faxno         = #{faxNo}
  , biz_ty        = #{bizType}
  , capl          = #{capital}
  , deal_ty       = #{deal_type}
  , yr_deal_scale = #{deal_scale}
  , prmy_mat      = #{material}
  , con_q         = #{conQ}
  , product       = #{product}
  , updt          = current_timestamp
  , joincmp       = #{joinCmp}
  , main_product  = #{mainProduct}
where bizno = #{bizNo}

update tb_pils_mbr
set nm            = #{name}
  , mbphno        = #{mbphno}
  , psitn_dept_nm = #{deptNm}
  , pstn          = #{position}
  , email_addr    = #{email}
  , telno         = #{memTelNo}
  , faxno         = #{memFaxNo}
  , upd_dt        = current_date
where bizno = #{bizNo}

mapper.select:
select user_id
from tb_pils_mbr
where user_id = #{id}

select bizno
from tb_pils_cmpmbr
where bizno = #{bizNo}

