       , #{telNo}
       , #{telNo}
       , #{bizType}
       , #{capital}
       , #{deal_type}
       , #{deal_scale}
       , #{material}
       , #{conQ}
       , current_timestamp
       ,#{product})

insert into tb_pils_mbr
( nm
, mbphno
, psitn_dept_nm
, pstn
, email_addr
, telno
, faxno
, auth_cd
, bizno
, user_id
, pswd
, join_dt
, manage_cd)
values ( #{name}
       , #{mbphno}
       , #{deptNm}
       , #{position}
       , #{email}
       , #{memTelNo}
       , #{memFaxNo}
       , 'M102'
       , #{bizNo}
       , #{id}
       , encode(encrypt(convert_to(#{passwd}, 'utf8'), 'ENC_KEY', 'aes'), 'hex')
       , current_date
       , #{management_cd})

INSERT INTO tb_pils_cmp_sttus (bizno, base_yy, ix_dvsn, ix_data,rgstdt) VALUES (#{bizNo}, 2019, 'M201', #{ix_data1},current_timestamp ), (#{bizNo}, 2019, 'M202', #{ix_data2},current_timestamp ), (#{bizNo}, 2019, 'M203', #{ix_data3},current_timestamp );
INSERT INTO tb_pils_cmp_sttus (bizno, base_yy, ix_dvsn, ix_data,rgstdt) VALUES (#{bizNo}, 2020, 'M201', #{ix_data4},current_timestamp ), (#{bizNo}, 2020, 'M202', #{ix_data5},current_timestamp ), (#{bizNo}, 2020, 'M203', #{ix_data6},current_timestamp );
INSERT INTO tb_pils_cmp_sttus (bizno, base_yy, ix_dvsn, ix_data,rgstdt) VALUES (#{bizNo}, 2021, 'M201', #{ix_data7},current_timestamp ), (#{bizNo}, 2021, 'M202', #{ix_data8},current_timestamp ), (#{bizNo}, 2021, 'M203', #{ix_data9},current_timestamp );

mapper.select:
select A.bizno as bizNo ,A.cmp_mbr_cd as mem_cd ,A.cmp_nm as cmpNm ,to_char(A.appdt, 'YYYY-MM-DD') as appdate ,B.nm as name ,B.user_id as id ,B.pswd as passwd
from tb_pils_cmpmbr A join tb_pils_mbr B on A.bizno =B.bizno where cmp_mbr_cd= #{mem_cd} and b.manage_cd ='M501' order by appdt desc
LIMIT #{pageUnit} OFFSET #{firstIndex}

mapper.update:
update tb_pils_cmpmbr
set cmp_nm        = #{cmpNm}
  , bizno         = #{bizNo}
  , ceo           = #{ceo}
  , fdate         = #{fdate}
  , addr          = #{address}
  , addr_dtl      = #{address_dtl}
  , telno         = #{telNo}
  , faxno         = #{faxNo}
  , biz_ty        = #{bizType}
  , capl          = #{capital}
  , deal_ty       = #{deal_type}
  , yr_deal_scale = #{deal_scale}
  , prmy_mat      = #{material}
  , con_q         = #{conQ}
  , product       = #{product}
  , updt          = current_timestamp
where bizno = #{bizNo}

update tb_pils_mbr
set nm            = #{name}
  , mbphno        = #{mbphno}
  , psitn_dept_nm = #{deptNm}
  , pstn          = #{position}
  , email_addr    = #{email}
  , telno         = #{memTelNo}
  , faxno         = #{memFaxNo}
  , upd_dt        = current_date
where bizno = #{bizNo}

update tb_pils_cmp_sttus set ix_data = #{ix_data1}
where bizno = #{bizNo} and base_yy= 2019 and ix_dvsn = 'M201';

update tb_pils_cmp_sttus set ix_data = #{ix_data2}
where bizno = #{bizNo} and base_yy= 2019 and ix_dvsn = 'M202';

update tb_pils_cmp_sttus set ix_data = #{ix_data3}
where bizno = #{bizNo} and base_yy= 2019 and ix_dvsn = 'M203';

update tb_pils_cmp_sttus set ix_data = #{ix_data4}
where bizno = #{bizNo} and base_yy= 2020 and ix_dvsn = 'M201';

update tb_pils_cmp_sttus set ix_data = #{ix_data5}
where bizno = #{bizNo} and base_yy= 2020 and ix_dvsn = 'M202';

update tb_pils_cmp_sttus set ix_data = #{ix_data6}
where bizno = #{bizNo} and base_yy= 2020 and ix_dvsn = 'M203';

update tb_pils_cmp_sttus set ix_data = #{ix_data7}
