mapper.select:
select case
           when inPass = pswd then 1
           else 0 end as rslt
from (select encode(encrypt(convert_to(#{passwd}, 'utf8'), 'ENC_KEY', 'aes'), 'hex') as inPass
           , pswd
      from tb_pils_mbr tpm
      where user_id = #{id}
     ) a;

select bizno as bizNo
from tb_pils_mbr tpm
where user_id = #{id}

select auth_cd as auth_cd
from tb_pils_mbr tpm
where user_id = #{id}

mapper.insert:
insert into tb_pils_mbr
( nm
, mbphno
, psitn_dept_nm
, pstn
, email_addr
, telno
, faxno
, auth_cd
, bizno
, user_id
, pswd
, join_dt
, manage_cd)
values ( #{name}
       , #{mbphno}
       , #{deptNm}
       , #{position}
       , #{email}
       , #{memTelNo}
       , #{memFaxNo}
       , 'M102'
       , #{bizNo}
       , #{id}
       , encode(encrypt(convert_to(#{passwd}, 'utf8'), 'ENC_KEY', 'aes'), 'hex')
       , current_date
       , #{management_cd})

mapper.update:
update tb_pils_mbr
set nm            = #{name}
  , mbphno        = #{mbphno}
  , psitn_dept_nm = #{deptNm}
  , pstn          = #{position}
  , email_addr    = #{email}
  , telno         = #{memTelNo}
  , faxno         = #{memFaxNo}
  , upd_dt        = current_date
where bizno = #{bizNo}
  and user_id = #{id}

update tb_pils_mbr
set pswd = encode(encrypt(convert_to(#{passwd}, 'utf8'), 'ENC_KEY', 'aes'), 'hex')
where user_id = #{id}
